CREATE TABLE help_subject(
    id int UNSIGNED NOT NULL AUTO_INCREMENT,
    name_en varchar (50) NULL,
    name_pt varchar (50) NULL,
    PRIMARY KEY (id)
);
​
CREATE TABLE help_subject_question(
    id int UNSIGNED NOT NULL AUTO_INCREMENT,
    description_en varchar (400) NULL,
    description_pt varchar (400) NULL,
    answer_en longtext NULL,
    answer_pt longtext NULL,
    subject_id int UNSIGNED NOT NULL,
    PRIMARY KEY (id),
    FOREIGN KEY (subject_id) REFERENCES help_subject(id)
);


